import React, { useEffect, useState  } from 'react';
import { Fragment } from 'react';
import {Link} from "react-router-dom"
import "./Acc.css"
import ProgressBar from 'react-bootstrap/ProgressBar';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  NavbarText,
  Container,
} from 'reactstrap';


function Acc(args) {
const now = 60; 

const [progress, setProgress] = useState(0);
const [nom, setNom] = useState(sessionStorage.getItem("FirstName")+" "+sessionStorage.getItem("LastName"));
 
let progressInterval = null;


  useEffect(() => {
    
   
    progressInterval = setInterval(() => {
      setProgress(prev => prev + 1);
    }, 1000);
    
  }, []);

  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => setIsOpen(!isOpen);

 
    return (
      <div className='body2' >
        {progress<100  &&
        <div className="row d-flex justify-content-center">
          <div className="col-8 myProg">
            <ProgressBar className="gg" now={progress} label={`${progress}%`}/>
          </div>
        </div>}
       {progress>100 &&
       <Navbar className="row p-2 d-flex" color="light">
        <NavbarBrand className='col-7 d-flex justify-content-start ms-4' href="/">
          <img alt="logo" src="Nexadz.png" className='' style={{height: 25,width: 100}}/>
        </NavbarBrand>
        <NavbarText className='col-3 d-flex justify-content-end'>
          <div className='d-flex'>
            <div><img alt="" src="person.svg" className=' p-2 mt-2 me-2 img1'/></div>
            <div className='me-3'>
              <div className='txt1'>Administrator</div>
              <div>
              <UncontrolledDropdown>
              <DropdownToggle nav caret>
                {nom}
              </DropdownToggle>
              <DropdownMenu right>
                <DropdownItem className='d-flex'><div><img alt="" src="person.svg" className='me-2'/></div><Link  className="lnk mb-2 ms-2" to={"/MyProfile"} >View profile</Link></DropdownItem>
                <DropdownItem divider />
                <DropdownItem className='d-flex'><div><img alt="" src="sigout.svg" className='me-3'/></div><div>Sign out</div></DropdownItem>
              </DropdownMenu>
            </UncontrolledDropdown>
              </div>
            </div>
          </div>
        </NavbarText>
    
    </Navbar>}
    </div>
      
      
    );
  }
  
  export default Acc;
