    //         .then(response => {
    //             console.log(response.data.rep2)
    //            if(response.data.rep2=="true"){
    //                 navigate("/Acc");
    //             }
    //             else{
    //                 alert("The URI already exists");
    //             }
                
    //         })
    //         .catch(err=>alert("Err"));
    //         }else{
    //             alert("The passwords are different"); 
    //         }
        
    //     }
        
        
        
       
        
    // }
    

    // //La fonction pour modifier les informations:
    const Changer = async (e) => {
        e.preventDefault();
        
    }
    
        
    
        return (
        <div className='body'>
        <div className="row justify-content-center">
           
                <div className="col-6 col-md-6 col-10 p-3 mt-lg-5  my-3 form1">
                
                <div className='row justify-content-center' >
                    <div className='col-8 titre mt-3' ><img src='Nexadz.png' width={140} height={30}/></div>
                </div>
                
                <form className='frm'>

                    <div class="row mt-1 ">
                        {/* <div class="form-label d-flex justify-content-start" >First name</div> */}
                        <div class="col-6"><input type="text" class="form-control " placeholder='First name' value={FirstName} onChange={(e) => setFirstName(e.target.value)}/></div>
                        <div class="col-6"><input type="text" class="form-control " placeholder='Last name' value={LastName} onChange={(e) => setLastName(e.target.value)}/></div>
                    </div>

                    <div class="row mt-3">
                        {/* <label class="form-label d-flex justify-content-start">First name</label> */}
                        <div class="col-6"><input type="email" class="form-control col-lg-5" placeholder='Email' value={Email} onChange={(e) => setEmail(e.target.value)}/></div>
                        <div class="col-6"><input type="text" class="form-control col-lg-5" placeholder='Phone number' value={Phone} onChange={(e) => setPhone(e.target.value)}/></div>
                    </div>

                    <div class="row mt-3 ">
                        {/* <label class="form-label d-flex justify-content-start">Email</label> */}
                        <div class="col-6"><input type="text" class="form-control" placeholder='Password' value={Password} onChange={(e) => setPassword(e.target.value)}/></div>
                    </div>

                    <div class="row mt-3 ">
                        {/* <label class="form-label d-flex justify-content-start">Phone number</label> */}
                        <div class="col-6"><input type="text" class="form-control" placeholder='Title' value={Title} onChange={(e) => setTitle(e.target.value)}/></div>
                        <div class="col-6"><input type="text" class="form-control" placeholder='Slogan' value={Slogan} onChange={(e) => setSlogan(e.target.value)}/></div>
                    </div>

                    <div class="row mt-3 ">
                        {/* <label class="form-label d-flex justify-content-start">Password</label> */}
                        <div class="col-lg-9 col-6"><input type="text" class="form-control" placeholder='URl' value={Uri} onChange={(e) => setUri(e.target.value)}/></div>
                        <div class="col-lg-3 col-6"><input type="text" class="form-control" placeholder='nexadz.com' disabled/></div>
                    </div>

                    <div class="row mt-3 ">
                        <label class="form-label d-flex justify-content-start text-secondary ms-2">Logo</label>
                        <div class="col-12"><input type="file" class="form-control" placeholder='Slogan' value={Slogan} onChange={(e) => setSlogan(e.target.value)}/></div>
                    </div>

                    <div class="row mt-3 p-3 d-flex justify-content-center">
                         <button className='btn btn-primary col-lg-4 ' >Back </button>
                         <button className='btn btn-primary col-lg-4 ms-lg-3 mt-lg-0 mt-2'> Change </button>
                    </div>
                </form>
    
                
                
    
    
    
    
            </div>
        </div>
           
        </div>
        );
     
        
      }
      
      export default MyProfile;
