import 'bootstrap/dist/css/bootstrap.min.css';
import './Login1.css';

import { useState,useRef,useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function MyProfile() {

    const [id,setId] = useState(sessionStorage.getItem("id"));
    const [FirstName, setFirstName] = useState('');
    const [LastName, setLastName] = useState('');
    const [Email, setEmail] = useState('');
    const [Phone, setPhone] = useState('');
    const [Password, setPassword] = useState('');
    const [Title, setTitle] = useState('');
    const [Slogan, setSlogan] = useState('');
    const [Url, setUrl] = useState('');
    

    //pour recevoir les informations de profil
    useEffect(() => {
        console.log(id);
        const urlB = "https://signup.nexadz.com/back/getAc.php";
        let fdata = new FormData();
        fdata.append("Id",id);
        axios.post(urlB,fdata)
            .then(response => {
               if(response.data.don=="false"){
                    alert("ggg")
                }
                else{
                    setFirstName(response.data.firstname);
                    setLastName(response.data.lastname);
                    setEmail(response.data.email);
                    setPassword(response.data.passworde);
                    setPhone(response.data.phone);
                    setTitle(response.data.title);
                    setSlogan(response.data.slogan);
                    setUrl(response.data.uri);
                }
                
            })
            .catch(err=>alert("Connection problem"));

        
