        e.preventDefault();
        
    }
    
        
    
        return (
        <div className='body'>
        <div className="row justify-content-center">
           
                <div className="col-6 col-md-6 col-10 p-3 mt-lg-5  my-3 form1">
                
                <div className='row justify-content-center' >
                    <div className='col-8 titre mt-3' ><img src='Nexadz.png' width={140} height={30}/></div>
                </div>
                
                <form className='frm'>

                    <div class="row mt-1 ">
                        {/* <div class="form-label d-flex justify-content-start" >First name</div> */}
                        <div class="col-6"><input type="text" class="form-control " placeholder='First name' value={FirstName} onChange={(e) => setFirstName(e.target.value)}/></div>
                        <div class="col-6"><input type="text" class="form-control " placeholder='Last name' value={LastName} onChange={(e) => setLastName(e.target.value)}/></div>
                    </div>

                    <div class="row mt-3">
                        {/* <label class="form-label d-flex justify-content-start">First name</label> */}
                        <div class="col-6"><input type="email" class="form-control col-lg-5" placeholder='Email' value={Email} onChange={(e) => setEmail(e.target.value)}/></div>
                        <div class="col-6"><input type="text" class="form-control col-lg-5" placeholder='Phone number' value={Phone} onChange={(e) => setPhone(e.target.value)}/></div>
                    </div>

                    <div class="row mt-3 ">
                        {/* <label class="form-label d-flex justify-content-start">Email</label> */}
